import React, { useState } from 'react';
import "./portifolio.css"
import Menu from './Menu';

export default function Portifolio() {
  const [items, setItems] = useState(Menu);
  const [activeFilter, setActiveFilter] = useState('Everything'); // Track active filter

  const filterItem = (categoryItem) => {
    let filteredItems = Menu;
    if (categoryItem === 'Favorite') {
      // Filter items marked as favorite
      filteredItems = Menu.filter((curElem) => curElem.favorite);
    } else {
      // Filter items based on category
      filteredItems = Menu.filter((curElem) => curElem.category === categoryItem);
    }
    setItems(filteredItems);
    setActiveFilter(categoryItem); // Update active filter
  };

  return (
    <section className="work container section" id="work">
      <h2 className="section_title">Recent Works</h2>
      <div className="work_filters">
        <span className={`work_item ${activeFilter === 'Everything' ? 'active' : ''}`} onClick={() => filterItem('Everything')}>Everything</span>
        <span className={`work_item ${activeFilter === 'FRONTEND' ? 'active' : ''}`} onClick={() => filterItem('FRONTEND')}>FrontEnd</span>
        <span className={`work_item ${activeFilter === 'BACKEND' ? 'active' : ''}`} onClick={() => filterItem('BACKEND')}>BackEnd</span>
        <span className={`work_item ${activeFilter === 'WEB3' ? 'active' : ''}`} onClick={() => filterItem('WEB3')}>Web3</span>
        <span className={`work_item ${activeFilter === 'Favorite' ? 'active' : ''}`} onClick={() => filterItem('Favorite')}>Favorite</span>
      </div>
      <div className="work_container grid">
        {items.map((elem) => {
          const { id, image, title, category, link } = elem;
          return (
            <div className="work_card" key={id}>
              <div className="work_thumbnail">
                <img src={image} alt="Project" className="work_img"/>
                <div className="work_mask"></div>
              </div>
              <span className="work_category">{category}</span>
              <h3 className="work_title">{title}</h3>
              <a href={link} target="_blank" rel="noopener noreferrer" className="work_button">
                <i className="icon-link work_button-icon"></i>
              </a>
            </div>
          );
        })}
      </div>
    </section>
  );
}
