import React from 'react'
import "./home.css"
import Me from '../../assets/fahadkAvatar1.png'
import HeaderSocials from './HeaderSocials';
import ScrollDown from './ScrollDown';
import Shapes from './Shapes';

export default function Home() {
  const [displayAlert, setDisplayAlert] = useState(true);

    const toggle = () => {
        setDisplayAlert(!displayAlert);
    }
  return (
    <section className='home container' id="home">
      <div className={displayAlert ? "alert show" : "alert"}>
            {displayAlert && (
                <span className="closebtn" onClick={toggle}>&times;</span>
            )}
            <strong>Notice</strong> Website Still Under Development.
        </div>
      <div className="intro">
        <img src={Me} alt="" className="home_img" />
        <h1 className="nome_name">Kabali Fahad Maliki</h1>
        <span className="home_education">I am a Software Engineer</span>

        <HeaderSocials/>
        <a href="#contact" className="btn">Let's connect</a>
        <ScrollDown/>
      </div>
      <Shapes/>
    </section>
  )
}
